#!/bin/bash\n\necho "Terminando i processi Node.js di Cursor..."\npkill -f "node.*cursor"\n\necho "Fermando il servizio cursor..."\nsystemctl stop cursor.service\n\necho "Pulizia finale dei processi residui..."\nsleep 2\npkill -9 -f "node.*cursor"\n\necho -e "\nVerifica finale:"\nif pgrep -f "node.*cursor" > /dev/null; then\n    echo "ATTENZIONE: Alcuni processi Node.js sono ancora in esecuzione:"\n    ps aux | grep node | grep -v grep\nelse\n    echo "Tutti i processi Node.js sono stati terminati con successo"\nfi
